targets.expr:
label_replace(
  up{job="apiserver"},
  "instance", "$1", "instance", "(.*):.*"
)
* on(instance) group_left(node) (
  label_replace(
    max by(internal_ip, node) (
      kube_node_info{job="kube-state-metrics", node=~".*master.*"}
    ),
    "instance", "$1", "internal_ip", "(.*)"
  )
)
